//variables/
var alm = msg.alm;
var temp =msg.temp;
var almLast = flow.get("alm") || alm;
var tempLast = flow.get("temp") || temp;

//filter arrays//

var almOldMap = new Map(almLast.map(item => [item.name, item.value]));
var almChanged = alm.filter(item => almOldMap.get(item.name) !== item.value);
var tempOldMap = new Map(tempLast.map(item => [item.name, item.value]));
var tempChanged = temp.filter(item => tempOldMap.get(item.name) !== item.value);




const alarmMap = new Map(almChanged.map(item => [item.name.replace("_Alm", ""), item.value]));

// Helper function to assign group
function getGroup(freezerId) {
    const num = parseInt(freezerId.replace("FFM_", ""));
    if (num >= 1 && num <= 50) return "A";
    if (num >= 51 && num <= 100) return "B";
    if (num >= 101 && num <= 150) return "C";
    if (num >= 151 && num <= 200) return "D";
    return "Unknown";
}

// alm arrays
const almArray = almChanged.map(almItem => {
    const freezerId = almItem.name.replace("_Alm", "");
    return [
        {
            alarmActive: almItem.value || 0 // Convert 0/1 to boolean
        },
        {
            group: getGroup(freezerId),
            freezerId: freezerId
        }
    ];
});
// temp arrays
const tempArray = tempChanged.map(tempItem => {
    const freezerId = tempItem.name.replace("_Temp", "");
    return [
        {
            temp_C: parseFloat(tempItem.value.toFixed(2))
        },
        {
            group: getGroup(freezerId),
            freezerId: freezerId
        }
    ];
});

//Merge arrays//
var merged = [...almArray,...tempArray];
msg.almArray = almArray
msg.tempArray = tempArray

//set measurment //

msg.measurement = "freezer_status";

//set flow vars//
flow.set("alm",alm);
flow.set("temp", temp);

msg.payload = merged

return msg;
